import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';

function OrdersChart() {
  const [chartData, setChartData] = useState(null); // default is null for safety
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('/api/orders/stats')
      .then(response => {
        const data = response.data;

        // Safely check if monthlyOrders exists
        if (data && data.monthlyOrders) {
          const labels = Object.keys(data.monthlyOrders);
          const counts = Object.values(data.monthlyOrders);

          setChartData({
            labels: data.dates,
            datasets: [
              {
                label: 'Orders',
                data: data.counts,
                borderColor: 'blue',
                fill: false,
              }
            ]
          });
          setLoading(false); 
        }
      })
      .catch(err => console.error('ðŸ“‰ Error fetching order stats:', err));
  }, []);

  return (
    <div>
      <h2>ðŸ“ˆ Orders Over Time</h2>
      {chartData.labels && chartData.datasets ?  (
  <Line data={chartData} />
) : (
  <p>Loading chart...</p>
)}
    </div>
  );
}

export default OrdersChart;
